@psychology_router.callback_query(F.data.startswith('mood_') & (F.data != 'mood_stats'))
async def cmd_save_mood(callback: CallbackQuery, session: AsyncSession):
    await callback.answer('')
    
    mood_map = {
        'mood_happy': 'üòä',
        'mood_good': 'üôÇ',
        'mood_neutral': 'üòê',
        'mood_sad': 'üòî',
        'mood_angry': 'üò§'
    }
    
    mood_type = callback.data  
    emoji = mood_map.get(mood_type, 'üòê')
    
    await req_save_mood_record(session, callback.from_user.id, mood_type, emoji)
    
    all_moods = await req_get_all_moods(session, callback.from_user.id)
    
    emoji_list = []
    for mood in all_moods:
        emoji_list.append(mood['emoji'])
    
    stats = await req_get_mood_statistics(session, callback.from_user.id)
    
    response = f"{emoji} –ù–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–æ!\n\n"
    
    if stats:
        most_common = stats['most_common']
        
        response += f"–í—Å–µ–≥–æ –∑–∞–ø–∏—Å–µ–π: {stats['total']}\n"
        response += f"–°–∞–º–æ–µ —á–∞—Å—Ç–æ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ: {most_common['emoji']} ({most_common['count']} —Ä–∞–∑)\n\n"
        
        response += "–í—Å–µ —Ç–≤–æ–∏ —Å–º–∞–π–ª—ã:\n"
        for i in range(0, len(emoji_list), 10):
            response += ''.join(emoji_list[i:i+10]) + "\n"
    else:
        response += "–≠—Ç–æ —Ç–≤–æ—ë –ø–µ—Ä–≤–æ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ! üéâ\n"
        response += f"–°–æ—Ö—Ä–∞–Ω–µ–Ω–æ: {emoji}"
    
    await callback.message.answer(response, reply_markup=kb.mood_keyboard)

@psychology_router.callback_query(F.data == 'mood_stats')
async def cmd_mood_stats(callback: CallbackQuery, session: AsyncSession):
    await callback.answer('')
    
    stats = await req_get_mood_statistics(session, callback.from_user.id)
    
    if not stats:
        response = "–£ —Ç–µ–±—è –ø–æ–∫–∞ –Ω–µ—Ç –∑–∞–ø–∏—Å–µ–π –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏—è.\n\n–î–æ–±–∞–≤—å –ø–µ—Ä–≤–æ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ, –≤—ã–±—Ä–∞–≤ —Å–º–∞–π–ª –≤—ã—à–µ! üòä"
        keyboard = kb.mood_keyboard
    else:
        response = "üìä –¢–≤–æ—è —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–π:\n\n"
        
        total = stats['total']
        for stat in stats['stats']:
            percentage = (stat['count'] / total) * 100
            response += f"{stat['emoji']} - {stat['count']} —Ä–∞–∑ ({percentage:.1f}%)\n"
        
        response += f"\n–í—Å–µ–≥–æ –∑–∞–ø–∏—Å–µ–π: {total}\n"
        response += f"–°–∞–º–æ–µ —á–∞—Å—Ç–æ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ: {stats['most_common']['emoji']}"
        
        keyboard = kb.mood_stats_keyboard
    
    await callback.message.answer(response, reply_markup=keyboard)

